        /* Premium Hero Slider Styles */
        .hero {
            position: relative;
            min-height: 100vh !important;
            display: flex;
            align-items: center;
            justify-content: center;
            overflow: hidden;
            background: #0f172a;
        }

        .hero-slider {
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            z-index: 0;
        }

        .hero-slide {
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background-size: cover;
            background-position: center;
            background-repeat: no-repeat;
            opacity: 0;
            transition: opacity 2s cubic-bezier(0.4, 0, 0.2, 1), transform 15s linear;
            transform: scale(1);
            will-change: transform, opacity;
        }

        .hero-slide.active {
            opacity: 1;
            transform: scale(1.15);
        }

        .hero-overlay-luxury {
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: radial-gradient(circle at center, rgba(15, 23, 42, 0.4) 0%, rgba(15, 23, 42, 0.7) 100%);
            z-index: 1;
        }

        .hero-content {
            position: relative;
            z-index: 10;
            text-align: center;
            color: white;
            padding: 0 var(--spacing-md);
            max-width: 1100px;
        }

        .premium-title {
            font-size: clamp(3rem, 10vw, 7rem);
            font-weight: 950;
            line-height: 0.85;
            margin-bottom: var(--spacing-xl);
            text-shadow: 0 20px 50px rgba(0, 0, 0, 0.6);
            letter-spacing: -3px;
        }

        .text-glow {
            background: linear-gradient(to right, #fff, var(--color-accent), #fff);
            -webkit-background-clip: text;
            background-clip: text;
            -webkit-text-fill-color: transparent;
            filter: drop-shadow(0 0 20px rgba(255, 255, 255, 0.2));
        }

        .premium-subtitle {
            font-size: var(--text-2xl);
            font-weight: 400;
            color: rgba(255, 255, 255, 0.95);
            max-width: 800px;
            margin: 0 auto var(--spacing-4xl);
            line-height: 1.6;
            text-shadow: 0 2px 10px rgba(0, 0, 0, 0.5);
            animation: lux-reveal-text 1.2s cubic-bezier(0.16, 1, 0.3, 1) 0.5s both;
        }

        .luxury-reveal .premium-title {
            animation: lux-reveal-text 1.2s cubic-bezier(0.16, 1, 0.3, 1) 0.2s both;
        }

        .luxury-reveal .hero-buttons {
            animation: lux-reveal-text 1.2s cubic-bezier(0.16, 1, 0.3, 1) 0.8s both;
            display: flex;
            gap: 20px;
            justify-content: center;
            flex-wrap: wrap;
        }

        @keyframes lux-reveal-text {
            0% {
                opacity: 0;
                transform: translate3d(0, 40px, 0);
            }

            100% {
                opacity: 1;
                transform: translate3d(0, 0, 0);
            }
        }

        .premium-title,
        .premium-subtitle,
        .hero-buttons {
            will-change: transform, opacity;
            backface-visibility: hidden;
            -webkit-backface-visibility: hidden;
        }

        .hero-indicators {
            position: absolute;
            bottom: 50px;
            left: 50%;
            transform: translateX(-50%);
            display: flex;
            gap: 15px;
            z-index: 15;
        }

        .indicator {
            width: 30px;
            height: 4px;
            background: rgba(255, 255, 255, 0.2);
            cursor: pointer;
            transition: all 0.4s ease;
            border-radius: 2px;
        }

        .indicator.active {
            background: #fff;
            width: 60px;
            box-shadow: 0 0 15px rgba(255, 255, 255, 0.5);
        }

        @media (max-width: 768px) {
            .premium-title {
                font-size: 3.5rem;
                letter-spacing: -1px;
            }

            .premium-subtitle {
                font-size: 1.15rem;
            }

            .hero-indicators {
                bottom: 30px;
            }
        }



        // Premium Hero Image Slider
        document.addEventListener('DOMContentLoaded', function () {
            const slides = document.querySelectorAll('.hero-slide');
            const indicators = document.querySelectorAll('.hero-indicators .indicator');
            let currentSlide = 0;
            const totalSlides = slides.length;
            let autoSlideInterval;

            function goToSlide(index) {
                slides[currentSlide].classList.remove('active');
                indicators[currentSlide].classList.remove('active');
                currentSlide = index;
                if (currentSlide >= totalSlides) currentSlide = 0;
                if (currentSlide  {
                indicator.addEventListener('click', () => {
                    stopAutoSlide();
                    goToSlide(index);
                    startAutoSlide();
                });
            });

            // Start auto-slide
            startAutoSlide();

            // Pause on hover
            const heroSection = document.getElementById('heroSection');
            if (heroSection) {
                heroSection.addEventListener('mouseenter', stopAutoSlide);
                heroSection.addEventListener('mouseleave', startAutoSlide);
            }
        });






